mapper.select:
SELECT count(employeeId) as recCount FROM cms_employee where 1 = 1
and employeeId = #{employeeId}
and name like concat('%',#{name},'%')
and DATE_FORMAT(updateDay, '%Y-%m-%d %H:%i:%s') = #{updateDay}

SELECT cms.employeeId as employeeId, cms.name, cms.sex, mst2.generalName as sexForList, DATE_FORMAT(cms.birthday, '%Y-%m-%d') as birthday, DATE_FORMAT(cms.joiningDate, '%Y-%m-%d') as joiningDate, cms.address, cms.phone, cms.mail, cms.jobType, cms.jobLevel, cms.salary, cms.employeeType, cms.hasTax, cms.topWorkHour, cms.downWorkHour, cms.loginId, cms.password, cms.employeeKbn, mst1.generalName as employeeKbnForList, DATE_FORMAT(cms.registDay, '%Y-%m-%d %H:%i:%s') as registDay, DATE_FORMAT(cms.updateDay, '%Y-%m-%d %H:%i:%s') as updateDay FROM cms_employee cms left join cms_general_mst mst1 on cms.employeeKbn = mst1.generalId and mst1.generalKey = 'EMPLOYEE_TYPE' left join cms_general_mst mst2 on cms.sex = mst2.generalId and mst2.generalKey = 'SEXY'
and employeeId = #{employeeId}
and name like concat('%',#{name},'%')
and employeeKbn = #{employeeKbn}
and sex = #{sex}
and birthday >= date_add(DATE_FORMAT(#{birthday}, '%Y-%m-%d'),interval -day(DATE_FORMAT(#{birthday}, '%Y-%m-%d'))+1 day) and birthday < last_day(DATE_FORMAT(#{birthday}, '%Y-%m-%d'))
and loginId = #{loginId}
