mapper.select:
SELECT sal.employeeId, sal.salaryMonth, CONCAT(sal.salary,"å††") as salary, sal.workhours, sal.disabledGeneration, sal.cost, sal.employeeId, sal.actualSalary, eml.name as employeeNm FROM cms_salary_detail sal left join cms_employee eml on sal.employeeId = eml.employeeId where 1=1
and sal.employeeId = #{employeeId}
and sal.salaryMonth =#{salaryMonth}
order by sal.employeeId asc, sal.salaryMonth DESC
